sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   try {
		g_form.clearMessages();
		var courseId = g_form.getValue("x_1535645_sparta_a_courses");
		if (courseId === "") {
			return;
		}

		var ga = new GlideAjax("CheckStudentCourse");
		ga.addParam("sysparm_name", "studentCourseExists")
		ga.addParam("sysparm_course_id", courseId);
		ga.addParam("sysparm_student_id", newValue);

		ga.getXMLAnswer(function(answer) {
			if (answer === "true") {
				g_form.addErrorMessage("This student has already enrolled in that course");
			}
		});

   } catch (err) {
		g_form.addErrorMessage("Error in Check Existing Student Course: " + err);
   }

}
