sys_script_include.script:
var CheckStudentCourse = Class.create();
CheckStudentCourse.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    studentCourseExists: function() {

        try {
            const courseId = this.getParameter("sysparm_course_id");
            const studentId = this.getParameter("sysparm_student_id");

            const matches = new global.GlideQuery("x_1535645_sparta_a_m2m_x_1535645_sp_x_1535645_sp")
                .where("x_1535645_sparta_a_student", studentId)
                .where("x_1535645_sparta_a_courses", courseId)
                .count();
            return matches > 0;
        } catch (err) {
            gs.error("Error in studentCourseExists: " + err);
        }
    },

    type: 'CheckStudentCourse'
});
